// File: contracts/LUKT_Presale.sol


pragma solidity ^0.8.25;

// Updated imports for non-upgradeable versions of IERC20 and SafeERC20



// Keeping upgradeable-specific imports for other features







contract TuckatonPresale is
    Initializable,
    ReentrancyGuardUpgradeable,
    OwnableUpgradeable,
    UUPSUpgradeable,
    PausableUpgradeable
{
    using SafeERC20 for IERC20;
    using SafeCast for uint256;

    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        _disableInitializers();
    }

    // Updated type declarations
    IERC20 public token;
    IERC20 public usdtToken;
    IERC20 public usdtToken2;
    AggregatorV3Interface public priceFeed;

    struct Stage {
        uint256 tokens;
        uint256 price;
    }

    Stage[13] public stages;
    uint256 public currentStage;

    event ApprovalRequested(address requester, uint256 amount);

    event TokensPurchasedWithUSDT(
        address indexed purchaser,
        uint256 usdtAmount,
        uint256 tokensTransferred
    );
    event TokensPurchasedWithETH(
        address indexed purchaser,
        uint256 ethAmount,
        uint256 tokensTransferred
    );

    bool public useOraclePrice;
    uint256 public manualEthPriceInUSDT;

    function initialize(
        address _token,
        address _usdtToken,
        address _usdtToken2,
        address _priceFeed,
        address _owner
    ) public initializer {
        __ReentrancyGuard_init();
        __Pausable_init();

        __Ownable_init(_owner);
        token = IERC20(_token);
        usdtToken = IERC20(_usdtToken);
        usdtToken2 = IERC20(_usdtToken2);
        priceFeed = AggregatorV3Interface(_priceFeed);

        useOraclePrice = true;
        resetStages();
    }

    function resetStages() public onlyOwner {
        stages[0] = Stage(10000000 * 10 ** 18, 21400); // Stage 0 price: 0.0214 USDT per token
        stages[1] = Stage(10000000 * 10 ** 18, 25000); // Stage 1 price: 0.0250 USDT per token
        stages[2] = Stage(10000000 * 10 ** 18, 27000); // Stage 2 price: 0.0270 USDT per token
        stages[3] = Stage(10000000 * 10 ** 18, 28000); // Stage 3 price: 0.0280 USDT per token
        stages[4] = Stage(10000000 * 10 ** 18, 31000); // Stage 4 price: 0.0310 USDT per token
        stages[5] = Stage(10000000 * 10 ** 18, 32000); // Stage 5 price: 0.0320 USDT per token
        stages[6] = Stage(20000000 * 10 ** 18, 33000); // Stage 6 price: 0.0330 USDT per token
        stages[7] = Stage(20000000 * 10 ** 18, 34000); // Stage 7 price: 0.0340 USDT per token
        stages[8] = Stage(50000000 * 10 ** 18, 37000); // Stage 8 price: 0.0370 USDT per token
        stages[9] = Stage(60000000 * 10 ** 18, 42000); // Stage 9 price: 0.0420 USDT per token
        stages[10] = Stage(70000000 * 10 ** 18, 45000); // Stage 10 price: 0.0450 USDT per token
        stages[11] = Stage(70000000 * 10 ** 18, 47000); // Stage 11 price: 0.0470 USDT per token
        stages[12] = Stage(10500000 * 10 ** 18, 52000); // Stage 12 price: 0.0520 USDT per token
    }

    function buyTokensWithETH() external payable whenNotPaused nonReentrant {
        require(msg.value > 0, "0ETH");
        uint256 contractTokenBalance = token.balanceOf(address(this));
        require(contractTokenBalance > 0, "presaleOver");
        uint256 ethDecimalFactor = 10 ** 20;
        uint256 ethPriceUsdt = getCurrentEthPriceInUSDT();
        uint256 usdtEquivalent = (msg.value * ethPriceUsdt) / ethDecimalFactor; // Convert ETH sent to equivalent USDT
        (uint256 tokensToSend, ) = calculateTokens(
            usdtEquivalent
        );

        require(
            tokensToSend <= contractTokenBalance,
            "insufficientTokenBalance"
        );

        token.safeTransfer(msg.sender, tokensToSend);
        updateStageData(tokensToSend);

        emit TokensPurchasedWithETH(msg.sender, msg.value, tokensToSend);
    }

    function buyTokensWithUSDT(
        uint256 usdtAmount
    ) external whenNotPaused nonReentrant {
        require(usdtAmount > 0, "0USDT");
        uint256 contractTokenBalance = token.balanceOf(address(this));
        require(contractTokenBalance > 0, "presaleOver");
        uint256 allowance = IERC20(usdtToken).allowance(
            msg.sender,
            address(this)
        );
        require(allowance >= usdtAmount, "insufficientAllowance");

        (uint256 tokensToSend, uint256 usdtSpent) = calculateTokens(usdtAmount);

        usdtToken.safeTransferFrom(msg.sender, address(this), usdtSpent);
        token.safeTransfer(msg.sender, tokensToSend);

        updateStageData(tokensToSend);
        emit TokensPurchasedWithUSDT(msg.sender, usdtSpent, tokensToSend);
    }

    function buyTokensWithUSDTV2(
        uint256 usdtAmount
    ) external whenNotPaused nonReentrant {
        require(usdtAmount > 0, "0USDT");
        uint256 contractTokenBalance = token.balanceOf(address(this));
        require(contractTokenBalance > 0, "presaleOver");
        uint256 allowance = IERC20(usdtToken2).allowance(
            msg.sender,
            address(this)
        );
        require(allowance >= usdtAmount, "insufficientAllowance");

        (uint256 tokensToSend, uint256 usdtSpent) = calculateTokens(usdtAmount);

        usdtToken2.safeTransferFrom(msg.sender, address(this), usdtSpent);
        token.safeTransfer(msg.sender, tokensToSend);

        updateStageData(tokensToSend);
        emit TokensPurchasedWithUSDT(msg.sender, usdtSpent, tokensToSend);
    }

    function calculateTokens(
        uint256 usdtAmount
    ) private view returns (uint256 tokensToTransfer, uint256 usdtSpent) {
        require(usdtAmount > 0, "0USDT");
        uint256 remainingUsdt = usdtAmount;
        uint256 stageIndex = currentStage;
        uint256 tokensToTransferTemp = 0;
        uint256 decimalFactor = 10 ** 18;

        while (stageIndex < stages.length && remainingUsdt > 0) {
            uint256 tokenPrice = stages[stageIndex].price;
            uint256 tokensAvailable = stages[stageIndex].tokens;
            uint256 totalCostForStageTokens = (tokensAvailable /
                decimalFactor) * tokenPrice;

            if (remainingUsdt >= totalCostForStageTokens) {
                tokensToTransferTemp += tokensAvailable;
                remainingUsdt -= totalCostForStageTokens;
                stageIndex++;
            } else {
                uint256 tokensFromStage = remainingUsdt / tokenPrice;
                remainingUsdt -= tokensFromStage * tokenPrice;
                tokensToTransferTemp += tokensFromStage * decimalFactor;
                break;
            }
        }
        require(tokensToTransferTemp > 0, "insufficientToken");
        usdtSpent = usdtAmount - remainingUsdt;
        return (tokensToTransferTemp, usdtSpent);
    }

    function updateStageData(uint256 tokensSold) private {
        uint256 remainingTokens = tokensSold;

        while (currentStage < stages.length && remainingTokens > 0) {
            Stage storage stage = stages[currentStage];
            if (stage.tokens <= remainingTokens) {
                remainingTokens -= stage.tokens;
                stage.tokens = 0;
                currentStage++;
            } else {
                stage.tokens -= remainingTokens;
                break;
            }
        }
    }

    function withdrawAllTuckaton() public onlyOwner {
        uint256 contractBalance = token.balanceOf(address(this));
        require(contractBalance > 0, "0Balance");

        IERC20(token).safeTransfer(msg.sender, contractBalance);
    }

    function withdrawERC20(
        address tokenAddress,
        uint256 amount
    ) external onlyOwner {
        IERC20 otherToken = IERC20(tokenAddress);
        otherToken.safeTransfer(owner(), amount);
    }

    function withdrawAllERC20(address tokenAddress) external onlyOwner {
        IERC20 withdrawToken = IERC20(tokenAddress);
        uint256 amountToWithdraw = withdrawToken.balanceOf(address(this));
        require(amountToWithdraw > 0, "0Balance");

        withdrawToken.safeTransfer(owner(), amountToWithdraw);
    }

    function setStagePrice(uint256 _stage, uint256 newPrice) public onlyOwner {
        require(_stage >= 0 && _stage <= 12, "indexInvalid");
        stages[_stage].price = newPrice;
    }

    function setStageTokens(
        uint256 _stage,
        uint256 newTokenAmount
    ) public onlyOwner {
        require(_stage >= 0 && _stage <= 12, "indexInvalid");
        stages[_stage].tokens = newTokenAmount;
    }

    function setCurrentStage(uint256 _stage) external onlyOwner {
        require(_stage >= 0 && _stage <= 12, "indexInvalid");
        currentStage = _stage;
    }

    function pause() public onlyOwner {
        _pause();
    }

    function unpause() public onlyOwner {
        _unpause();
    }

    // Function to check the contract's balance of the TuckatonPresale token
    function presaleTokenBalance() public view returns (uint256) {
        return token.balanceOf(address(this));
    }

    // Function to check the contract's balance of USDT
    function usdtBalance() public view returns (uint256) {
        return usdtToken.balanceOf(address(this));
    }

    // Function to check the contract's balance of USDT2
    function usdt2Balance() public view returns (uint256) {
        return usdtToken2.balanceOf(address(this));
    }

    function ethBalance() public view returns (uint256) {
        return address(this).balance;
    }

    /**
     * @dev Returns detailed information about the current presale stage, including
     *      the stage index, number of tokens available, and the price per token.
     * @return stageIndex The index of the current stage.
     * @return tokensAvailable The number of tokens available in the current stage.
     * @return pricePerToken The price of each token in the current stage.
     */
    function getCurrentStage()
        public
        view
        returns (
            uint256 stageIndex,
            uint256 tokensAvailable,
            uint256 pricePerToken
        )
    {
        require(currentStage < stages.length, "stagesCompleted");
        Stage memory current = stages[currentStage];
        return (currentStage, current.tokens, current.price);
    }

    /**
     * @dev Returns the balance of any ERC20 token held by the contract.
     * @param tokenAddress The address of the ERC20 token.
     * @return The balance of the specified token held by the contract.
     */
    function getTokenBalance(
        address tokenAddress
    ) public view returns (uint256) {
        IERC20 storedToken = IERC20(tokenAddress);
        return storedToken.balanceOf(address(this));
    }

    /**
     * @dev Allows the owner to switch between using the oracle price and a manually set price.
     * @param _useOracle Indicates whether the oracle price should be used.
     */
    function setUseOraclePrice(bool _useOracle) external onlyOwner {
        useOraclePrice = _useOracle;
    }

    /**
     * @dev Allows the owner to set the manual ETH price in USDT.
     * @param _price The manual price to set.
     */
    function setManualEthPriceInUSDT(uint256 _price) external onlyOwner {
        manualEthPriceInUSDT = _price;
    }

    /**
     * @dev Calculates the total price for a given number of tokens considering the current stage pricing.
     *      This function handles payments in both USDT and ETH by converting ETH to USDT based on the latest
     *      oracle price if needed. The function iterates over the available token stages until all requested
     *      tokens are accounted for or stages are exhausted.
     * @param numTokens The number of tokens for which to calculate the price.
     * @param payInETH Whether the payment is intended to be made in ETH. If false, the price is returned in USDT.
     * @return price The total price of the requested tokens, in USDT or ETH equivalent.
     */
    function getPriceForTokens(
        uint256 numTokens,
        bool payInETH
    ) public view returns (uint256 price) {
        require(numTokens > 0, "0Token");
        require(currentStage < stages.length, "stagesCompleted");

        uint256 remainingTokens = numTokens;
        uint256 totalPriceInUSDT = 0;
        uint256 stageIndex = currentStage;
        while (remainingTokens > 0 && stageIndex < stages.length) {
            Stage memory stage = stages[stageIndex];
            if (stage.tokens == 0) {
                stageIndex++;
                continue; // Skip stages with no tokens available
            }

            uint256 tokensToPrice = (remainingTokens <= stage.tokens)
                ? remainingTokens
                : stage.tokens;
            totalPriceInUSDT += tokensToPrice * stage.price;
            remainingTokens -= tokensToPrice;
            if (remainingTokens > 0) {
                stageIndex++;
            }
        }

        require(remainingTokens == 0, "fundsExceedSupply");

        if (payInETH) {
            (, int256 oracleEthPriceInUSDT, , , ) = priceFeed.latestRoundData();
            require(oracleEthPriceInUSDT > 0, "priceError");
            return (totalPriceInUSDT * 10 ** 2) / uint256(oracleEthPriceInUSDT);
        } else {
            return totalPriceInUSDT / 10 ** 18;
        }
    }

    /**
     * @dev Determines the number of tokens that can be bought with a specified amount of funds in USDT or ETH.
     *      If paying in ETH, funds are first converted to USDT based on the current ETH/USDT price from an oracle.
     *      The function iterates through the stages, calculating how many tokens can be purchased with the available funds
     *      at each stage's token price until funds are depleted or sufficient tokens are purchased.
     * @param funds The amount of funds available for purchasing tokens, denominated in USDT or ETH.
     * @param payInETH True if the funds are provided in ETH, requiring conversion to USDT based on the oracle price.
     * @return tokens The total number of tokens that can be purchased with the provided funds.
     */
    function getTokensForFunds(
        uint256 funds,
        bool payInETH
    ) public view returns (uint256 tokens) {
        require(currentStage < stages.length, "stagesCompleted");

        uint256 fundsInUSDT;
        if (payInETH) {
            // Convert ETH to USDT using the oracle price
            (, int256 oracleEthPriceInUSDT, , , ) = priceFeed.latestRoundData();
            require(oracleEthPriceInUSDT > 0, "invalidPrice");
            fundsInUSDT = (funds * uint256(oracleEthPriceInUSDT)) / 1e20;
        } else {
            fundsInUSDT = funds;
        }

        // Use calculateTokens function to determine how many tokens can be bought with the funds in USDT
        (uint256 tokensToTransfer, ) = calculateTokens(fundsInUSDT);
        return tokensToTransfer;
    }

    /**
     * @dev Retrieves the current price of ETH in USDT. This function will return the price from an oracle
     *      if the contract is set to use the oracle price; otherwise, it will return a manually set price.
     *      This is useful for pricing ETH in terms of USDT for calculations throughout the contract.
     * @return The current price of ETH in USDT, either from the oracle or the manually set value.
     */
    function getCurrentEthPriceInUSDT() public view returns (uint256) {
        require((useOraclePrice || manualEthPriceInUSDT > 0), "priceError");
        if (useOraclePrice) {
            (, int256 price, , , ) = priceFeed.latestRoundData();
            require(price > 0, "invalidPrice");
            return uint256(price);
        } else {
            return manualEthPriceInUSDT;
        }
    }

    /**
     * @dev Calculates the USDT equivalent of the sent ETH based on the current price feed.
     * @return usdtEquivalent The calculated USDT equivalent of the sent ETH.
     */
    function getUsdtEquivalentForEth(
        uint256 amountETH
    )
        external
        whenNotPaused
        nonReentrant
        returns (uint256)
    {
        require(amountETH > 0, "0ETH");
        uint256 ethDecimalFactor = 10 ** 20;
        // Get the current ETH price in USDT from the price feed
        uint256 ethPriceUsdt = getCurrentEthPriceInUSDT();

        // Calculate the equivalent amount of USDT for the sent ETH
        uint256 usdtEquivalent = (amountETH * ethPriceUsdt) / ethDecimalFactor;

        // Return the calculated USDT equivalent
        return usdtEquivalent;
    }

    /**
     * @dev Checks if a user has approved the contract to spend a specified natural amount of USDT on their behalf,
     *      where the natural amount is adjusted to include the token's decimals.
     *      This function is useful for verifying that the contract can legally move the specified USDT funds
     *      from a user's account before attempting operations that require spending USDT.
     * @param user The address of the user whose USDT allowance is to be checked.
     * @param amount The natural amount of USDT (not considering decimals) to check for allowance.
     * @return True if the user has allowed the contract to handle at least the specified amount of USDT (considering decimals), otherwise it throws an error.
     */
    function hasApprovalForUsdt(
        address user,
        uint256 amount
    ) external
        whenNotPaused
        nonReentrant 
        returns (bool) {
        require(amount > 0, "incorrectAmount"); // Ensuring the amount is positive
        uint256 amountWithDecimals = amount; // Adjust amount to include USDT's decimal places
        uint256 allowance = usdtToken.allowance(user, address(this));
        require(allowance >= amountWithDecimals, "insufficientAllowance");
        return true;
    }

    function hasApprovalForUsdt2(
            address user,
            uint256 amount
        ) external
            whenNotPaused
            nonReentrant 
            returns (bool) {
            require(amount > 0, "incorrectAmount"); // Ensuring the amount is positive
            uint256 amountWithDecimals = amount; // Adjust amount to include USDT's decimal places
            uint256 allowance = usdtToken2.allowance(user, address(this));
            require(allowance >= amountWithDecimals, "insufficientAllowance");
            return true;
        }

    /**
     * @dev Withdraws all ETH from the contract to the owner's address. This function is restricted to
     *      the owner of the contract and is intended for withdrawing contract balances securely.
     *      The function checks the contract's balance, ensures there's ETH to withdraw, and then attempts
     *      to transfer all ETH to the contract owner.
     * @notice Only the contract owner can call this function.
     */
    function withdrawAllETH() public onlyOwner {
        // Check the contract's balance
        uint256 balance = address(this).balance;
        require(balance > 0, "0ETH");

        // Transfer Ether to the owner
        (bool sent, ) = owner().call{value: balance}("");
        require(sent, "sentETHFailed");
    }

    function _authorizeUpgrade(
        address newImplementation
    ) internal override onlyOwner {}
}
