// File: Stake.sol


pragma solidity ^0.8.24;



contract StakeContract is Initializable {
    IERC20 public styleToken;
    IERC20 public rewardToken;

    mapping(uint => uint) public lockAmounts;
    mapping(uint => uint[3]) public lockDurations;
    address public admin;

    struct Stake {
        address staker;
        uint256 amount;
        uint256 unlockTime;
        uint256 lastClaimed;
        uint256 tier;
    }
    mapping(address => Stake) public stakers;

    function initialize(
        address _styleToken,
        address _rewardToken,
        address _admin,
        uint256[] memory _amounts,
        uint256[] memory _durations,
        uint256[] memory _tier3Durations
    ) external initializer {
        admin = _admin;
        styleToken = IERC20(_styleToken);
        rewardToken = IERC20(_rewardToken);
        lockDurations[1][0] = _durations[0];
        lockDurations[2][0] = _durations[1];
        for (uint i = 0; i < _tier3Durations.length; i++) {
            lockAmounts[i + 1] = _amounts[i];
            lockDurations[3][i] = _tier3Durations[i];
        }
    }

    function getLockDurations(
        uint tier
    ) external view returns (uint[3] memory) {
        return lockDurations[tier];
    }

    function stake(uint256 tier) external {
        require(tier >= 1 && tier <= 3, "Invalid tier");
        require(stakers[msg.sender].amount == 0, "User already staked");
        Stake memory userStake;
        uint256 lockTime = block.timestamp + lockDurations[tier][0] * 1 days;

        styleToken.transferFrom(
            msg.sender,
            address(this),
            lockAmounts[tier] * 10 ** 18
        );
        userStake.amount = lockAmounts[tier];
        userStake.unlockTime = lockTime;
        userStake.lastClaimed = block.timestamp;
        userStake.tier = tier;
        stakers[msg.sender] = userStake;
    }

    function unstake() external {
        Stake memory userStake = stakers[msg.sender];
        require(userStake.amount != 0, "User didn't stake");
        require(
            userStake.unlockTime < block.timestamp,
            "User can't unstake now"
        );
        delete stakers[msg.sender];
        styleToken.transfer(msg.sender, userStake.amount * 10 ** 18);
    }

    function withdrawRewards() external {
        uint256 amount = calculateRewards();
        require(amount > 0, "Insufficient Rewards");
        // check later
        // delete stakes[msg.sender];
        stakers[msg.sender].lastClaimed = block.timestamp;
        rewardToken.transferFrom(admin, msg.sender, amount);
    }

    function calculateRewards() public view returns (uint256) {
        uint256 totalRewards = 0;
        Stake memory currentStake = stakers[msg.sender];
        if (currentStake.tier == 3) {
            require(
                block.timestamp - currentStake.lastClaimed >=
                    lockDurations[3][0] * 1 days,
                "Unelligible for claim yet"
            );
            totalRewards = block.timestamp - currentStake.lastClaimed <
                lockDurations[3][1] * 1 days
                ? (currentStake.amount * 10 ** 18 * 5) / 100
                : (
                    block.timestamp - currentStake.lastClaimed <=
                        lockDurations[3][2] * 1 days
                        ? (currentStake.amount * 10 ** 18 * 12) / 100
                        : (currentStake.amount * 10 ** 18 * 20) / 100
                );
        }
        return totalRewards;
    }
}

// File: StakeFactory.sol


pragma solidity ^0.8.24;





contract StakeFactory is Ownable {
    address public immutable stakeImplementation;
    address private immutable _owner;

    mapping(address => address[]) public stakers;
    address[] private stakes;

    constructor() Ownable(msg.sender) {
        stakeImplementation = address(new StakeContract());
    }

    function getStakes() external view returns (address[] memory) {
        return stakers[msg.sender];
    }

    function getTotalStakes() external view returns (address[] memory) {
        return stakes;
    }

    function createStake(
        address _styleToken,
        address _rewardsToken,
        uint[] memory _amounts,
        uint[] memory _durations,
        uint[] memory _tier3Durations
    ) external returns (address) {
        require(_amounts.length == _tier3Durations.length, "Incorrect Length");
        require(_amounts.length == 3, "Incorrect Amount Length");
        require(_durations.length == 2, "Incorrect Durations Length");
        // uint balance = IERC20(_token).balanceOf(msg.sender);
        // require(
        //     balance >= lockAmounts[_tier] * 10 ** 18,
        //     "Insufficient balance Token from admin"
        // );
        bytes32 salt = keccak256(abi.encodePacked(msg.sender, stakes.length));
        address clone = Clones.cloneDeterministic(stakeImplementation, salt);
        stakers[msg.sender].push(clone);
        stakes.push(clone);
        // IERC20(_token).transferFrom(
        //     msg.sender,
        //     clone,
        //     lockAmounts[_tier] * 10 ** 18
        // );
        StakeContract(clone).initialize(
            _styleToken,
            _rewardsToken,
            msg.sender,
            _amounts,
            _durations,
            _tier3Durations
        );
        return clone;
    }
}
