      "content": "// SPDX-License-Identifier: MIT

pragma solidity ^0.8.22;

import { SafeERC20, IERC20 } from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

// solhint-disable-next-line no-unused-import
import { IOFT, SendParam } from "@layerzerolabs/lz-evm-oapp-v2/contracts/oft/interfaces/IOFT.sol";

import { IDonate, Donation, Currency } from "./IDonate.sol";

/**
 * @title DonateCore
 */
abstract contract DonateCore is IDonate {
    using SafeERC20 for IERC20;

    address public immutable donationReceiver;

    // stargate instances that will be used to move donations in the event this is inherited by the DonateRemote.sol
    address public immutable stargateUsdc;
    address public immutable stargateUsdt;
    address public immutable stargateNative;

    IERC20 public immutable tokenUsdc;
    IERC20 public immutable tokenUsdt;

    // Keep track of user donations
    mapping(address stargate => mapping(address user => uint256 amountDonated)) public donations;

    // We pass stargate to ensure there is no config mismatch with regards to stargate and corresponding token address
    constructor(address _donationReceiver, address _stargateUsdc, address _stargateUsdt, address _stargateNative) {
        // The end address whom receives the donations collected in this contract
        if (_donationReceiver == address(0)) revert InvalidDonationReceiver();
        donationReceiver = _donationReceiver;

        if (_stargateUsdc != address(0)) {
            stargateUsdc = _stargateUsdc;
            tokenUsdc = IERC20(IOFT(stargateUsdc).token());
        }

        if (_stargateUsdt != address(0)) {
            stargateUsdt = _stargateUsdt;
            tokenUsdt = IERC20(IOFT(stargateUsdt).token());
        }

        // There is no token for native because donations are only accepted in native, eg. NOT 'WETH'
        if (_stargateNative != address(0)) {
            stargateNative = _stargateNative;
            if (IOFT(stargateNative).token() != address(0)) {
                revert InvalidNativeStargate();
            }
        }
    }

    // returns the donation amounts for a given user
    function getDonation(address _user) external view returns (Donation memory) {
        return
            Donation(donations[stargateUsdc][_user], donations[stargateUsdt][_user], donations[stargateNative][_user]);
    }

    // Donate either USDC, USDT or Native depending if its supported on the chain
    // Allowed to specify a beneficiary in the event you want to donate on the behalf of someone else
    function donate(Currency _currency, uint256 _amount, address _beneficiary) external payable {
        if (_currency == Currency.USDC && stargateUsdc != address(0)) {
            if (msg.value != 0) revert UnexpectedMsgValue();
            tokenUsdc.safeTransferFrom(msg.sender, address(this), _amount);
            donations[stargateUsdc][_beneficiary] += _amount;
        } else if (_currency == Currency.USDT && stargateUsdt != address(0)) {
            if (msg.value != 0) revert UnexpectedMsgValue();
            tokenUsdt.safeTransferFrom(msg.sender, address(this), _amount);
            donations[stargateUsdt][_beneficiary] += _amount;
        } else if (_currency == Currency.Native && stargateNative != address(0)) {
            if (msg.value != _amount) revert InsufficientMsgValue();
            donations[stargateNative][_beneficiary] += _amount;
        } else {
            // sanity just in case somehow a different currency is somehow passed
            revert UnsupportedCurrency(_currency);
        }

        emit Donated(_currency, msg.sender, _beneficiary, _amount);
    }
}
