    modifier checkAmount(uint256 amount) {
        require(amount > 0, "amount must be greater than zero");
        _;
    }

    // "transfer token is the zero address"
    modifier checkTokenAddress(address tokenAddress){
        require(tokenAddress != address(0), "transfer token is the zero address");
        _;
    }

    modifier checkWithdrawAddress(address withdrawAddress){
        require(withdrawAddress != address(0), "withdraw address is the zero address");
        _;
    }

    modifier checkAddress(address newAddress){
        require(newAddress != address(0), "address is the zero address");
        _;
    }

}
// File: contracts/BlockATMDefi.sol


pragma solidity >=0.8.0 <=0.8.17;




interface AavePool {

    function supply(address asset, uint256 amount, address onBehalfOf, uint16 referralCode) external;

    function withdraw(address asset, uint256 amount, address to) external returns (uint256);
}

contract BlockATMDefi is Ownable,BlockUtils {
    using SafeERC20 for IERC20;

    address public aavePool;
    address public collectAddress;

    event BlockFinancial(address indexed toAddr,address indexed tokenAddr,uint256 amount,uint8 plaType);
    event SetAavePool(address indexed aavePool,address owner);
    event SetCollectAddress(address indexed aavePool,address owner);

    constructor(address newAavePool,address newCollectAddress) checkAddress(newAavePool) checkAddress(newCollectAddress) {
        aavePool = newAavePool;
        collectAddress = newCollectAddress;
    }

    modifier onlyCollect() {
        require(collectAddress == _msgSender(), "BlockATMDefi: caller is not the collect address");
        _;
    }

    function setCollectAddress(address newCollectAddress) public checkAddress(newCollectAddress) onlyOwner {
        collectAddress = newCollectAddress;
        emit SetCollectAddress(newCollectAddress,owner());
    }

    function financial(address asset, uint256 amount,address onBehalfOf,uint8 plaType) public onlyCollect returns (bool) {
        // appove
        IERC20(asset).safeIncreaseAllowance(aavePool,amount);
        AavePool(aavePool).supply(asset,amount,onBehalfOf,0);
        emit BlockFinancial(onBehalfOf, asset, amount, plaType);
        return true;
    }

}
