// File: ethethaiexchange.sol


pragma solidity 0.8.20;




contract Exchange is Ownable {

    IERC20Metadata public rewardToken;
    AggregatorV3Interface public priceFeed = AggregatorV3Interface(0x0567F2323251f0Aab15c8dFb1967E4e8A7D42aeE);
    
    uint256 public rate = 10000; // $1 : token
    uint256 public minPrice = 5 * (10 ** 8); // least pay price: $5


    constructor(IERC20Metadata _token) Ownable(msg.sender) {
        rewardToken = _token;
    }

    function updateToken(IERC20Metadata _token) external onlyOwner {
        rewardToken = _token;
    }

    function updateRate(uint256 _rate) external onlyOwner {
        require(_rate >= 10, "under 0.001");
        rate = _rate;
    }

    function withdrawToken() external onlyOwner {
        uint256 balance = rewardToken.balanceOf(address(this));
        rewardToken.transfer(msg.sender, balance);
    }

    function updateMinPrice(uint256 _price) external onlyOwner {
        minPrice = _price;
    }

    function withdrawETH() external onlyOwner {
        payable(msg.sender).transfer(address(this).balance);
    }

    function calculatePrice(uint256 ethValue) public view returns(uint256) {
        (,int256 answer,,,) = priceFeed.latestRoundData();
        uint256 ethPrice = uint256(answer);
        
        return (ethPrice * ethValue) / 1 ether;
    }

    function updatePriceFeed(address feed) external onlyOwner {
        priceFeed = AggregatorV3Interface(feed);
    }

    receive() external payable { 
        uint256 ethValue = msg.value;
        uint256 price = calculatePrice(ethValue);
        require(price >= minPrice, "need to satisfy min price at least");
        uint256 decimal = rewardToken.decimals();
        uint256 rewardAmount = (price * rate * (10 ** decimal)) / (10 ** 8 * 10000);
        rewardToken.transfer(msg.sender, rewardAmount);
    }
}
