// File: @layerzerolabs/solidity-examples/contracts/lzApp/NonblockingLzApp.sol



pragma solidity ^0.8.0;



/*
 * the default LayerZero messaging behaviour is blocking, i.e. any failed message will block the channel
 * this abstract class try-catch all fail messages and store locally for future retry. hence, non-blocking
 * NOTE: if the srcAddress is not configured properly, it will still block the message pathway from (srcChainId, srcAddress)
 */
abstract contract NonblockingLzApp is LzApp {
    using ExcessivelySafeCall for address;

    constructor(address _endpoint) LzApp(_endpoint) {}

    mapping(uint16 => mapping(bytes => mapping(uint64 => bytes32))) public failedMessages;

    event MessageFailed(uint16 _srcChainId, bytes _srcAddress, uint64 _nonce, bytes _payload, bytes _reason);
    event RetryMessageSuccess(uint16 _srcChainId, bytes _srcAddress, uint64 _nonce, bytes32 _payloadHash);

    // overriding the virtual function in LzReceiver
    function _blockingLzReceive(
        uint16 _srcChainId,
        bytes memory _srcAddress,
        uint64 _nonce,
        bytes memory _payload
    ) internal virtual override {
        (bool success, bytes memory reason) = address(this).excessivelySafeCall(
            gasleft(),
            150,
            abi.encodeWithSelector(this.nonblockingLzReceive.selector, _srcChainId, _srcAddress, _nonce, _payload)
        );
        if (!success) {
            _storeFailedMessage(_srcChainId, _srcAddress, _nonce, _payload, reason);
        }
    }

    function _storeFailedMessage(
        uint16 _srcChainId,
        bytes memory _srcAddress,
        uint64 _nonce,
        bytes memory _payload,
        bytes memory _reason
    ) internal virtual {
        failedMessages[_srcChainId][_srcAddress][_nonce] = keccak256(_payload);
        emit MessageFailed(_srcChainId, _srcAddress, _nonce, _payload, _reason);
    }

    function nonblockingLzReceive(
        uint16 _srcChainId,
        bytes calldata _srcAddress,
        uint64 _nonce,
        bytes calldata _payload
    ) public virtual {
        // only internal transaction
        require(_msgSender() == address(this), "NonblockingLzApp: caller must be LzApp");
        _nonblockingLzReceive(_srcChainId, _srcAddress, _nonce, _payload);
    }

    //@notice override this function
    function _nonblockingLzReceive(
        uint16 _srcChainId,
        bytes memory _srcAddress,
        uint64 _nonce,
        bytes memory _payload
    ) internal virtual;

    function retryMessage(
        uint16 _srcChainId,
        bytes calldata _srcAddress,
        uint64 _nonce,
        bytes calldata _payload
    ) public payable virtual {
        // assert there is message to retry
        bytes32 payloadHash = failedMessages[_srcChainId][_srcAddress][_nonce];
        require(payloadHash != bytes32(0), "NonblockingLzApp: no stored message");
        require(keccak256(_payload) == payloadHash, "NonblockingLzApp: invalid payload");
        // clear the stored message
        failedMessages[_srcChainId][_srcAddress][_nonce] = bytes32(0);
        // execute the message. revert if it fails again
        _nonblockingLzReceive(_srcChainId, _srcAddress, _nonce, _payload);
        emit RetryMessageSuccess(_srcChainId, _srcAddress, _nonce, payloadHash);
    }
}

// File: contracts/BridgeV3.sol


pragma solidity ^0.8.22;





interface IBridgeableToken is IERC20 {
    function mintBridge(address account, uint256 amount) external;

    function burnBridge(address account, uint256 amount) external;
}

/**
 * @title BridgeContract
 * @notice This contract is similar to BridgeContract.sol but has introduced a change to require users to pay a one-time activation fee to be able to use the bridge functionality.
 */
contract BridgeContract is OApp {
    IBridgeableToken public token;
    address payable private feeReceiver;

    struct ActivationInfo {
        bool isActivated;
        uint256 activationTime;
    }

    uint256 public activationFee;
    bool public requiresActivation = true;
    uint256 public activationDuration = 24 hours;

    mapping(address => ActivationInfo) public activatedAddresses;

    constructor(
        address _token,
        address _endpoint,
        address _owner,
        address payable _feeReceiver,
        uint256 _initialActivationFee
    ) OApp(_endpoint, _owner) Ownable(_owner) {
        token = IBridgeableToken(_token);
        feeReceiver = _feeReceiver;
        activationFee = _initialActivationFee;
    }

    event TokenSent(address indexed sender, uint32 dstEid, uint256 amount);
    event TokenReceived(address indexed receiver, uint256 amount);

    modifier checkActivation() {
        if (requiresActivation) {
            require(
                isActivated(msg.sender),
                "Address not activated or activation expired"
            );
        }
        _;
    }

    function estimateFees(
        uint32 _dstEid,
        string memory _message,
        uint256 _amount,
        bytes calldata _options
    ) public view returns (uint256 totalFee) {
        bytes memory payload = abi.encode(msg.sender, _amount, _message);
        MessagingFee memory messagingFee = _quote(
            _dstEid,
            payload,
            _options,
            false
        );
        totalFee = uint256(messagingFee.nativeFee);

        return (totalFee);
    }

    function sendMessage(
        uint32 _dstEid,
        string memory _message,
        uint256 _amount,
        bytes calldata _options
    ) public payable checkActivation {
        require(
            token.balanceOf(msg.sender) >= _amount,
            "Insufficient token balance"
        );
        // Burn tokens on the source chain
        token.burnBridge(msg.sender, _amount);

        bytes memory payload = abi.encode(msg.sender, _amount, _message);
        _lzSend(
            _dstEid,
            payload,
            _options,
            MessagingFee(msg.value, 0),
            payable(msg.sender)
        );

        emit TokenSent(msg.sender, _dstEid, _amount);
    }

    /**
     * @dev Called when data is received from the protocol. It overrides the equivalent function in the parent contract.
     * Protocol messages are defined as packets, comprised of the following parameters.
     * @param payload Encoded message.
     */
    function _lzReceive(
        Origin calldata, // A struct containing information about where the packet came from.
        bytes32, // A global unique identifier for tracking the packet
        bytes calldata payload,
        address, // Executor address as specified by the OApp.
        bytes calldata // Any extra data or options to trigger on receipt.
    ) internal override {
        // Decode the payload to get the message
        // In this case, type is string, but depends on your encoding!

        // Decode the payload to get the sender address and amount
        (address receiver, uint256 amount) = abi.decode(
            payload,
            (address, uint256)
        );
        activatedAddresses[msg.sender] = ActivationInfo(true, block.timestamp);
        emit TokenReceived(receiver, amount);

        // Mint tokens on the destination chain
        token.mintBridge(receiver, amount);
    }

    /**
     * @notice Sets the peer address (OApp instance) for a corresponding endpoint.
     * @param _eid The endpoint ID.
     * @param _peer The address of the peer to be associated with the corresponding endpoint.
     *
     * @dev Only the owner/admin of the OApp can call this function.
     * @dev Indicates that the peer is trusted to send LayerZero messages to this OApp.
     * @dev This is a modified version taken from OAppCore to take _peer as type address and then convert it to bytes32 for easier use.
     * @dev Set this to bytes32(0) to remove the peer address.
     * @dev Peer is a bytes32 to accommodate non-evm chains.
     */
    function setPeerAddress(uint32 _eid, address _peer) public onlyOwner {
        bytes32 peer = bytes32(uint256(uint160(_peer)));
        _setPeer(_eid, peer);
    }

    function activateBridge() public payable {
        require(requiresActivation, "Activation is currently disabled");
        require(!isActivated(msg.sender), "Address already activated");
        require(msg.value == activationFee, "Incorrect activation fee");

        activatedAddresses[msg.sender] = ActivationInfo(true, block.timestamp);

        // Transfer the fee to the fee receiver
        (bool sent, ) = feeReceiver.call{value: msg.value}("");
        require(sent, "Failed to send activation fee");
    }

    function isActivated(address _address) public view returns (bool) {
        ActivationInfo memory info = activatedAddresses[_address];
        return
            info.isActivated &&
            (block.timestamp - info.activationTime) <= activationDuration;
    }

    function setRequiresActivation(bool _requiresActivation) public onlyOwner {
        requiresActivation = _requiresActivation;
    }

    function setActivationFee(uint256 _newFee) public onlyOwner {
        activationFee = _newFee;
    }

    function getActivationFee() public view returns (uint256) {
        return activationFee;
    }

    function setActivationDuration(uint256 _newDuration) public onlyOwner {
        activationDuration = _newDuration;
    }

    function setFeeReceiver(address _feeReceiver) external onlyOwner {
        require(
            _feeReceiver != address(this),
            "Invalid Fee Receiver, cannot be contract"
        );
        require(
            _feeReceiver != address(0),
            "Invalid Fee Receiver, cannot be address 0"
        );
        feeReceiver = payable(_feeReceiver);
    }

    function setTokenAddress(address _token) public onlyOwner {
        token = IBridgeableToken(_token);
    }
}
