// File contracts/SNDNFTMinter.sol

// Original license: SPDX_License_Identifier: MIT
// Compatible with OpenZeppelin Contracts ^5.0.0
pragma solidity ^0.8.20;
interface ISNDNFT is IERC721 {
    function safeMint(address to) external returns (uint256 tokenId);

    function safeBatchMint(address to, int32 amount) external;
}

contract SNDNFTMinter is Ownable, EIP712 {
    using ECDSA for bytes32;
    using SafeERC20 for IERC20;
    address public treasurer;
    address public signer;
    uint32 public MaxWhiteList = 1000;
    uint32 public MaxSuperWhiteList = 3000;
    uint32 public mintedWhiteList;
    uint32 public mintedSuperWhiteList;
    uint32 public whitelistStart;
    uint32 public whitelistEnd;

    ISNDNFT public nft;
    uint256 public ethPrice;
    IERC20 public USDC;
    uint256 public usdcPrice;
    mapping(address => uint256) public nonces;

    event Received(address who, uint amount);
    event Withdraw(address who, uint amount);
    event Minted(address recipient, uint256 tokenId, uint8 category);

    struct MintInfo {
        address recipient;
        uint256 nonce;
        uint8 category;
        uint32 expired;
    }

    bytes32 constant MINTINFO_TYPEHASH =
        keccak256(
            "MintInfo(address recipient,uint256 nonce,uint8 category,uint32 expired)"
        );

    constructor(
        address _treasurer,
        address _signer,
        address _nft,
        uint256 _ethPrice,
        address _usdc,
        uint256 _usdcPrice
    ) EIP712("Swords Dungeons NFT Minter", "v1") Ownable(msg.sender) {
        treasurer = _treasurer;
        nft = ISNDNFT(_nft);
        ethPrice = _ethPrice;
        signer = _signer;
        usdcPrice = _usdcPrice;
        USDC = IERC20(_usdc);
    }

    receive() external payable {
        payable(treasurer).transfer(msg.value);
        emit Received(msg.sender, msg.value);
    }

    function withdraw(address payable to) external onlyOwner {
        uint amount = address(this).balance;
        to.transfer(amount);
        emit Withdraw(to, amount);
    }

    function setPrice(
        uint256 _ethPrice,
        uint256 _usdcPrice
    ) external onlyOwner {
        ethPrice = _ethPrice;
        usdcPrice = _usdcPrice;
    }

    function setSigner(address _signer) public onlyOwner {
        require(_signer != address(0));
        signer = _signer;
    }

    function setTreasurer(address _treasurer) public onlyOwner {
        require(_treasurer != address(0));
        treasurer = _treasurer;
    }

    // for test
    function setMax(
        uint32 _whiteList,
        uint32 _superWhiteList
    ) external onlyOwner {
        MaxWhiteList = _whiteList;
        MaxSuperWhiteList = _superWhiteList;
    }

    function setTime(uint32 _wlStart, uint32 _wlEnd) public onlyOwner {
        whitelistStart = _wlStart;
        whitelistEnd = _wlEnd;
    }

    // max super white list decrease, max white list increase the number
    function changeMax(uint32 _num) public onlyOwner {
        MaxSuperWhiteList -= _num;
        MaxWhiteList += _num;
        require(MaxSuperWhiteList >= mintedSuperWhiteList);
    }

    function airdrop(address[] memory _tos) external onlyOwner {
        for (uint32 i = 0; i < _tos.length; i++) {
            uint256 tokenId = nft.safeMint(_tos[i]);
            emit Minted(_tos[i], tokenId, 0);
        }
        mintedWhiteList += uint32(_tos.length);
        require(
            MaxWhiteList >= mintedWhiteList,
            "SNDNFTMinter: reach max, can't mint"
        );
    }

    // regular mint after white list minted
    function mint(bool payEth) external payable {
        require(block.timestamp > whitelistEnd, "SNDNFTMinter: not start yet");
        address recipient = _msgSender();
        if (payEth) {
            require(msg.value >= ethPrice, "SNDNFTMinter: insufficient eth");
            payable(treasurer).transfer(ethPrice);
            if (msg.value > ethPrice) {
                uint256 rest = msg.value - ethPrice;
                payable(recipient).transfer(rest);
            }
        } else {
            USDC.safeTransferFrom(recipient, treasurer, usdcPrice);
        }
        uint256 tokenId = nft.safeMint(recipient);
        mintedWhiteList++;
        require(
            MaxWhiteList >= mintedWhiteList,
            "SNDNFTMinter: reach max, can't mint"
        );
        emit Minted(recipient, tokenId, 3);
    }

    // white list mint, category: 1-regular whitelist, 2-super whitelist;
    function permitMint(
        bool payEth,
        uint8 category,
        uint32 expired,
        bytes memory signature
    ) external payable {
        require(
            block.timestamp >= whitelistStart &&
                block.timestamp <= whitelistEnd,
            "SNDNFTMinter: not within the whitelist minting period"
        );

        address recipient = _msgSender();
        MintInfo memory info = MintInfo({
            recipient: recipient,
            nonce: nonces[recipient],
            category: category,
            expired: expired
        });
        require(verify(info, signature), "SNDNFTMinter: signature is wrong!");
        require(expired > block.timestamp, "SNDNFTMinter: expired!");

        if (payEth) {
            require(msg.value >= ethPrice, "SNDNFTMinter: insufficient eth");
            payable(treasurer).transfer(ethPrice);
            if (msg.value > ethPrice) {
                uint256 rest = msg.value - ethPrice;
                payable(recipient).transfer(rest);
            }
        } else {
            USDC.safeTransferFrom(recipient, treasurer, usdcPrice);
        }

        uint256 tokenId = nft.safeMint(recipient);
        emit Minted(recipient, tokenId, category);

        nonces[recipient] += 1;
        if (category == 1) {
            mintedWhiteList++;
            require(
                MaxWhiteList >= mintedWhiteList,
                "SNDNFTMinter: reach max, can't mint"
            );
        } else {
            mintedSuperWhiteList++;
            require(
                MaxSuperWhiteList >= mintedSuperWhiteList,
                "SNDNFTMinter: reach max, can't mint"
            );
        }
    }

    function getNonce(address user) public view returns (uint256 nonce) {
        nonce = nonces[user];
    }

    function hash(MintInfo memory info) internal pure returns (bytes32) {
        return
            keccak256(
                abi.encode(
                    MINTINFO_TYPEHASH,
                    info.recipient,
                    info.nonce,
                    info.category,
                    info.expired
                )
            );
    }

    function verify(
        MintInfo memory info,
        bytes memory signature
    ) internal view returns (bool) {
        bytes32 digest = _hashTypedDataV4(hash(info));
        address addr = digest.recover(signature);
        return addr == signer;
    }

    function getPools()
        public
        view
        returns (uint32 maxSwl, uint32 maxWl, uint32 mintedSwl, uint32 mintedWl)
    {
        maxSwl = MaxSuperWhiteList;
        maxWl = MaxWhiteList;
        mintedSwl = mintedSuperWhiteList;
        mintedWl = mintedWhiteList;
    }
}
