    uint256 public refPercent = 10;

    mapping(address => bool) public whitelist;

    address public devAddress = 0xb340b57C673B1B8F1A606849fbFA4b39c409586D;

    address public usdt = address(0);

    struct Ref {
        uint256 ethAmount;
        uint256 usdAmount;
        uint256 count;
    }

    mapping(address => Ref) public refs;

    mapping(address => uint256) public specialRefs;

    modifier whenNotPaused(address from, address to) {
        require(
            !paused || whitelist[from] || whitelist[to],
            "Transfer is paused"
        );
        _;
    }

    constructor(address _usdt)
        ERC721A("Aurory AI Meta Node", "AMN", 1000, MAX_TOKENS)
    {
        require(_usdt != address(0));
        usdt = _usdt;
        _setDefaultRoyalty(msg.sender, 500);
        whitelist[msg.sender] = true;
        // devAddress = devAddress == address(0) ? msg.sender : devAddress;
    }

    function mint(uint256 _numberMint) public onlyOwner {
        _safeMint(msg.sender, _numberMint);
    }

    function withdraw() public onlyOwner {
        require(address(this).balance > 0, "Invalid amount");
        TransferHelper.safeTransferETH(msg.sender, address(this).balance);
    }

    function setDevAddress(address _devAddress) public onlyOwner {
        require(_devAddress != address(0));
        devAddress = _devAddress;
    }

    function setUsd(address _usdt) public onlyOwner {
        usdt = _usdt;
    }

    function setWhitelist(address _addr, bool _status) public onlyOwner {
        whitelist[_addr] = _status;
    }

    function setPaused(bool _paused) public onlyOwner {
        paused = _paused;
    }

    function setBaseURI(string memory newURI) public onlyOwner {
        baseURI = newURI;
    }

    function setRefPercent(uint256 _value) public onlyOwner {
        require(_value < 50 && _value < 50);
        refPercent = _value;
    }

    function setSpecialRef(address _ref, uint256 _percent) public onlyOwner {
        require(_percent < 50);
        specialRefs[_ref] = _percent;
    }

    function setTokenPrice(uint256 _price) public onlyOwner {
        tokenPrice = _price;
    }

    mapping(uint256 => bool) public usedNonce;

    function mintMetaNode(
        uint256 _numberMint,
        address _refAddress,
        uint256 _amount,
        uint256 _payment,
        uint256 _nonce,
        bytes calldata _signature //, string calldata _code
    ) public payable {
        require(
            verify(
                owner(),
                msg.sender,
                _numberMint,
                _payment,
                _amount,
                _nonce,
                _signature
            ) && usedNonce[_nonce] == false,
            "Invalid mint"
        );

        uint256 refAmount = 0;
        refAmount += transferToRef(_refAddress, _amount, _payment, msg.sender);
        safeTransfer(_amount - refAmount, _payment, msg.sender, devAddress);

        _safeMint(msg.sender, _numberMint);

        usedNonce[_nonce] = true;
        emit Mint(msg.sender, _numberMint, _amount, _refAddress, _payment);
    }

    function safeTransfer(
        uint256 _amount,
        uint256 _payment,
        address _sender,
        address _receiver
    ) internal {
        if (_payment == 1) {
            TransferHelper.safeTransferETH(_receiver, _amount);
        } else {
            TransferHelper.safeTransferFrom(usdt, _sender, _receiver, _amount);
        }
    }

    function transferToRef(
        address _ref,
        uint256 _amount,
        uint256 _payment,
        address _sender
    ) internal returns (uint256 refAmount) {
        if (_ref != address(0) && _ref != _sender) {
            uint256 percent = specialRefs[_ref] > 0
                ? specialRefs[_ref]
                : refPercent;
            if (percent > 0) {
                refAmount = (_amount * percent) / 100;
                if (_payment == 1) {
                    refs[_ref].ethAmount += refAmount;
                } else {
                    refs[_ref].usdAmount +=
                        (refAmount * 10**6) /
                        (10**IERC20(usdt).decimals());
                }
                refs[_ref].count += 1;
                safeTransfer(refAmount, _payment, _sender, _ref);
            }
            return refAmount;
        }
    }

    function tokenURI(uint256 _tokenId)
        public
        view
        override
        returns (string memory)
    {
        require(_exists(_tokenId), "Token does not exist.");
        bytes memory dataURI = abi.encodePacked(
            '{"name":"Aurory AI Meta Node',
            " #",
            _tokenId.toString(),
            '"',
            ',"image":"',
            baseURI,
            '"',
            "}"
        );
        return
            string(
                abi.encodePacked(
                    "data:application/json;base64,",
                    Base64.encode(dataURI)
                )
            );
    }

    function numberMinted(address owner) public view returns (uint256) {
        return _numberMinted(owner);
    }

    function supportsInterface(bytes4 interfaceId)
        public
        view
        virtual
        override(ERC721A, ERC2981)
        returns (bool)
    {
        return super.supportsInterface(interfaceId);
    }

    function setRoyaltyInfo(address receiver, uint96 feeBasisPoints)
        external
        onlyOwner
    {
        _setDefaultRoyalty(receiver, feeBasisPoints);
    }

    function setApprovalForAll(address operator, bool approved)
        public
        override
        whenNotPaused(operator, address(0))
    {
        super.setApprovalForAll(operator, approved);
    }

    function approve(address operator, uint256 tokenId)
        public
        override
        whenNotPaused(operator, address(0))
    {
        super.approve(operator, tokenId);
    }

    function transferFrom(
        address from,
        address to,
        uint256 tokenId
    ) public override whenNotPaused(from, to) {
        super.transferFrom(from, to, tokenId);
    }

    function safeTransferFrom(
        address from,
        address to,
        uint256 tokenId
    ) public override whenNotPaused(from, to) {
        super.safeTransferFrom(from, to, tokenId);
    }

    function safeTransferFrom(
        address from,
        address to,
        uint256 tokenId,
        bytes memory data
    ) public override whenNotPaused(from, to) {
        super.safeTransferFrom(from, to, tokenId, data);
    }

    receive() external payable {}
}
